import{_ as l,o,c as i,ah as r}from"./chunks/framework.PKn7PQPU.js";const p=JSON.parse('{"title":"默认配置项","description":"","frontmatter":{},"headers":[],"relativePath":"cn/_common/controlPanel/defaultItems.md","filePath":"cn/_common/controlPanel/defaultItems.md"}'),n={name:"cn/_common/controlPanel/defaultItems.md"};function s(g,t,e,a,u,d){return o(),i("div",null,[...t[0]||(t[0]=[r('<h1 id="默认配置项" tabindex="-1">默认配置项 <a class="header-anchor" href="#默认配置项" aria-label="Permalink to &quot;默认配置项&quot;">​</a></h1><p>控制面板内置了一系列系统级的配置开关，涵盖了视觉风格、交互体验和布局模式三大类。这些选项会根据当前的运行环境和授权状态智能显示或隐藏。</p><h2 id="视觉与特效" tabindex="-1">视觉与特效 <a class="header-anchor" href="#视觉与特效" aria-label="Permalink to &quot;视觉与特效&quot;">​</a></h2><ul><li><p><strong>主题 (Theme)</strong></p><ul><li><strong>类型</strong>: 单选 (Radio) / 色块选择</li><li><strong>说明</strong>: 切换当前应用的主题风格（如天穹、夜阑）。</li><li><strong>显隐逻辑</strong>: 如果系统仅注册了一个主题，该选项会自动隐藏。</li></ul></li><li><p><strong>3D 面板 (3D Panel)</strong></p><ul><li><strong>类型</strong>: 开关 (Switch)</li><li><strong>说明</strong>: 开启或关闭 SciFi 面板的 3D 透视变形效果。开启后面板会跟随鼠标位置产生细微的 3D 视差。</li><li><strong>显隐逻辑</strong>: 仅在开启了高级特性 (<code>isActAdvFeatures</code>) 且 3D 引擎初始化成功后显示。</li></ul></li><li><p><strong>蒙版模糊 (Mask Blur)</strong></p><ul><li><strong>类型</strong>: 开关 (Switch)</li><li><strong>说明</strong>: 控制弹窗、侧边栏或遮罩层的背景是否启用高斯模糊（Backdrop Filter）。关闭可提高低端设备的渲染性能。</li></ul></li></ul><h2 id="交互体验" tabindex="-1">交互体验 <a class="header-anchor" href="#交互体验" aria-label="Permalink to &quot;交互体验&quot;">​</a></h2><ul><li><p><strong>转场动画 (Transition)</strong></p><ul><li><strong>类型</strong>: 开关 (Switch)</li><li><strong>说明</strong>: 全局开启或关闭路由跳转和状态切换时的视图过渡动画。</li><li><strong>显隐逻辑</strong>: 仅在支持 View Transitions API 的浏览器（如 Chrome/Edge）中显示。在 Firefox 等非 Webkit 内核浏览器中自动隐藏。</li></ul></li><li><p><strong>提示音 (Sound)</strong></p><ul><li><strong>类型</strong>: 开关 (Switch)</li><li><strong>说明</strong>: 全局开启或关闭系统组件（如消息通知、点击反馈）的声音效果。</li></ul></li><li><p><strong>导航菜单触发 (Navigation Trigger)</strong></p><ul><li><strong>类型</strong>: 单选 (Radio)</li><li><strong>说明</strong>: 控制侧边栏或顶部导航的子菜单触发方式。 <ul><li><strong>点击 (Click)</strong>: 点击父级菜单展开子级。</li><li><strong>悬停 (Hover)</strong>: 鼠标悬停展开子级。</li></ul></li></ul></li></ul><h2 id="布局与适配" tabindex="-1">布局与适配 <a class="header-anchor" href="#布局与适配" aria-label="Permalink to &quot;布局与适配&quot;">​</a></h2><ul><li><p><strong>自适应模式 (Adaptive)</strong></p><ul><li><strong>类型</strong>: 单选 (Radio)</li><li><strong>说明</strong>: 控制 <code>TuiAdaptive</code> 面板的缩放策略。 <ul><li><strong>延伸 (Extension)</strong>: 保持比例，多余空间留白或延伸背景。</li><li><strong>弹性 (Flexible)</strong>: 智能缩放，尝试填满屏幕。</li><li><strong>固定 (Fixed)</strong>: 严格保持设计稿比例。</li><li><strong>拉伸 (Stretch)</strong>: 强制拉伸填满屏幕（可能会变形）。</li><li><strong>关闭 (Disabled)</strong>: 关闭自适应功能，按原始像素渲染。</li></ul></li></ul></li><li><p><strong>全局背景 (Background)</strong></p><ul><li><strong>类型</strong>: 单选 (Radio)</li><li><strong>说明</strong>: 控制背景层的渲染策略。 <ul><li><strong>所有 (All)</strong>: 显示底层 (Root) 和前置 (View) 背景。</li><li><strong>前置 (View)</strong>: 仅显示内容区背景。</li><li><strong>后置 (Root)</strong>: 仅显示根背景。</li><li><strong>无 (None)</strong>: 关闭背景渲染。</li></ul></li></ul></li><li><p><strong>浮动位置 (Floater Insert)</strong></p><ul><li><strong>类型</strong>: 单选 (Radio)</li><li><strong>说明</strong>: 控制浮动元素（如弹窗、Tooltip）挂载的 DOM 节点位置。 <ul><li><strong>适配面板 (#tuiAdpt)</strong>: 挂载在自适应容器内，随容器缩放。</li><li><strong>根节点 (#tuiRoot)</strong>: 挂载在 Body 根节点，不受缩放影响。</li></ul></li><li><strong>显隐逻辑</strong>: 当自适应模式为“关闭”时，该选项隐藏。</li></ul></li></ul>',8)])])}const h=l(n,[["render",s]]);export{p as __pageData,h as default};
