import{_ as r,o,c as n,ah as s}from"./chunks/framework.PKn7PQPU.js";const b=JSON.parse('{"title":"组件库对比 Comparison","description":"","frontmatter":{"pageClass":"doc-wide-table"},"headers":[],"relativePath":"cn/auth/platform/compare.md","filePath":"cn/auth/platform/compare.md"}'),a={name:"cn/auth/platform/compare.md"};function d(e,t,i,g,l,h){return o(),n("div",null,[...t[0]||(t[0]=[s('<h1 id="组件库对比-comparison" tabindex="-1">组件库对比 Comparison <a class="header-anchor" href="#组件库对比-comparison" aria-label="Permalink to &quot;组件库对比 Comparison&quot;">​</a></h1><p>TechUI 提供了四个针对不同场景的组件库。<strong>Prime</strong> 是全能的付费旗舰，而 <strong>Scifi</strong>、<strong>Base</strong> 和 <strong>Admin</strong> 则是专注于特定领域的免费核心库（支持通过授权解锁高级权益）。</p><h2 id="核心差异一览" tabindex="-1">核心差异一览 <a class="header-anchor" href="#核心差异一览" aria-label="Permalink to &quot;核心差异一览&quot;">​</a></h2><table tabindex="0"><thead><tr><th>特性 / 组件库</th><th>💎 Prime (旗舰版)</th><th>🚀 Scifi (科幻版)</th><th>📦 Base (基础版)</th><th>💼 Admin (管理版)</th></tr></thead><tbody><tr><td><strong>核心定位</strong></td><td><strong>全能集合</strong> <br>(可视化系统的一站式方案)</td><td><strong>视觉核心</strong> <br>(SVG 动态节点与科幻 UI)</td><td><strong>数据展现</strong> <br>(高密度数据展示基础库)</td><td><strong>业务框架</strong> <br>(中后台系统解决方案)</td></tr><tr><td><strong>适用场景</strong></td><td>复杂大屏、指挥中心、<br>既要酷炫又要业务的系统</td><td>纯数据可视化大屏、<br>UI 换肤、视觉层构建</td><td>仪表盘 (Dashboard)、<br>数据分析、常规 Web 应用</td><td>后台管理系统、<br>SaaS 平台、工作台</td></tr><tr><td><strong>许可机制</strong></td><td><strong>重许可 (Heavy)</strong> <br>(需 Keychain + Client Key)</td><td><strong>轻许可 (Lite)</strong> <br>(仅需 License 字符串)</td><td><strong>轻许可 (Lite)</strong> <br>(仅需 License 字符串)</td><td><strong>轻许可 (Lite)</strong> <br>(仅需 License 字符串)</td></tr><tr><td><strong>免费策略</strong></td><td><strong>无</strong> <br>(仅支持 10 分钟体验模式)</td><td><strong>核心免费</strong> <br>(永久可用，高级组件需授权)</td><td><strong>核心免费</strong> <br>(永久可用，高级组件需授权)</td><td><strong>核心免费</strong> <br>(永久可用，高级组件需授权)</td></tr></tbody></table><h2 id="功能覆盖范围" tabindex="-1">功能覆盖范围 <a class="header-anchor" href="#功能覆盖范围" aria-label="Permalink to &quot;功能覆盖范围&quot;">​</a></h2><table tabindex="0"><thead><tr><th>功能模块</th><th>💎 Prime</th><th>🚀 Scifi</th><th>📦 Base</th><th>💼 Admin</th></tr></thead><tbody><tr><td><strong>科幻 UI 组件</strong> <br>(面板/装饰/按钮/几何)</td><td>✅ <strong>包含</strong></td><td>✅ <strong>核心</strong></td><td>❌</td><td>❌</td></tr><tr><td><strong>数据展现组件</strong> <br>(表格/表单/工具栏)</td><td>✅ <strong>包含</strong></td><td>❌</td><td>✅ <strong>核心</strong></td><td>✅ <strong>包含</strong></td></tr><tr><td><strong>业务框架组件</strong> <br>(登录/路由/权限/布局)</td><td>✅ <strong>包含</strong></td><td>❌</td><td>⚠️ 部分</td><td>✅ <strong>核心</strong></td></tr><tr><td><strong>高级组件</strong> <br>(T3D/PanicAlert/TuiEcharts/...)</td><td>🔓 <strong>解锁</strong></td><td>⏳ 限时体验</td><td>⏳ 限时体验</td><td>⏳ 限时体验</td></tr><tr><td><strong>自适应适配面板</strong> <br>(TuiAdaptive)</td><td>✅</td><td>✅</td><td>✅</td><td>✅</td></tr></tbody></table><h2 id="详细说明" tabindex="-1">详细说明 <a class="header-anchor" href="#详细说明" aria-label="Permalink to &quot;详细说明&quot;">​</a></h2><h3 id="prime-旗舰版" tabindex="-1">Prime (旗舰版) <a class="header-anchor" href="#prime-旗舰版" aria-label="Permalink to &quot;Prime (旗舰版)&quot;">​</a></h3><ul><li><strong>定位</strong>：TechUI 的完全体。它是 Scifi 的视觉、Base 的数据能力与 Admin 的业务逻辑的超集。</li><li><strong>独占权益</strong>：默认解锁所有 <strong>高级组件</strong>（如 3D 面板、Echarts 深度封装）的库，无需额外配置 features 开关。</li><li><strong>门槛</strong>：必须付费授权，且集成时需要额外的 Keychain 依赖。</li></ul><h3 id="scifi-科幻版" tabindex="-1">Scifi (科幻版) <a class="header-anchor" href="#scifi-科幻版" aria-label="Permalink to &quot;Scifi (科幻版)&quot;">​</a></h3><ul><li><strong>定位</strong>：<strong>“只有皮囊”</strong>。专攻视觉效果，提供大量基于 SVG 的动态科幻面板和控件。</li><li><strong>缺失</strong>：不包含表格、表单等复杂的数据录入与展示组件。通常需要搭配其他组件库使用。</li><li><strong>权益</strong>：动态SVG UI 组件永久免费，仅 6 个高级组件受限。</li></ul><h3 id="base-基础版" tabindex="-1">Base (基础版) <a class="header-anchor" href="#base-基础版" aria-label="Permalink to &quot;Base (基础版)&quot;">​</a></h3><ul><li><strong>定位</strong>：<strong>“只有灵魂”</strong>。专攻数据密度与交互，类似 Element Plus 但针对大屏场景优化。</li><li><strong>缺失</strong>：不包含科幻风格的装饰性 UI 组件。</li><li><strong>权益</strong>：核心的数据组件永久免费。</li></ul><h3 id="admin-管理版" tabindex="-1">Admin (管理版) <a class="header-anchor" href="#admin-管理版" aria-label="Permalink to &quot;Admin (管理版)&quot;">​</a></h3><ul><li><strong>定位</strong>：<strong>“业务骨架”</strong>。在 Base 的基础上增加了中后台所需的登录页、动态路由守卫、KeepAlive 管理等逻辑。</li><li><strong>依赖</strong>：底层依赖 Base 组件库。</li></ul>',15)])])}const m=r(a,[["render",d]]);export{b as __pageData,m as default};
